import base64
import tempfile

import pytest
from sqlalchemy import Column, Integer, String, select
from sqlalchemy.orm import Session, declarative_base
from sqlalchemy_file.exceptions import (
    DurationValidationError,
)
from sqlalchemy_file.storage import StorageManager
from sqlalchemy_file.types import AudioField
from sqlalchemy_file.validators import AudioValidator

from tests.utils import get_test_container, get_test_engine

engine = get_test_engine()
Base = declarative_base()


@pytest.fixture
def fake_audio_short():  # duration=1s
    file = tempfile.NamedTemporaryFile(suffix=".wav")
    data = base64.b64decode(
        ""
    )
    file.write(data)
    file.seek(0)
    return file


@pytest.fixture
def fake_audio_long():  # duration=2.1s
    file = tempfile.NamedTemporaryFile(suffix=".wav")
    data = base64.b64decode(
        ""
    )
    file.write(data)
    file.seek(0)
    return file


@pytest.fixture
def fake_valid_audio():  # duration=2s
    file = tempfile.NamedTemporaryFile(suffix=".wav")
    data = base64.b64decode(
        ""
    )
    file.write(data)
    file.seek(0)
    return file


class AudioBook(Base):
    __tablename__ = "audiobook"

    id = Column(Integer, autoincrement=True, primary_key=True)
    title = Column(String(100), unique=True)
    audio = Column(
        AudioField(
            audio_validator=AudioValidator(min_ms=1500, max_ms=2000)  # 1.5s  # 2.5s
        )
    )

    def __repr__(self):
        return f"<AudioBook: id {self.id} ; name: {self.title}; audio {self.audio};>"  # pragma: no cover


class TestAudioValidator:
    def setup_method(self, method) -> None:
        Base.metadata.create_all(engine)
        StorageManager._clear()
        StorageManager.add_storage("test", get_test_container("test-audio-validator"))

    def test_min_duration_validation(self, fake_audio_short) -> None:
        with Session(engine) as session:
            session.add(AudioBook(title="Pointless Meetings", audio=fake_audio_short))
            with pytest.raises(
                DurationValidationError,
                match="Minimum allowed duration is: 1500, but 1000 is given.",
            ):
                session.flush()

    def test_max_duration_validation(self, fake_audio_long) -> None:
        with Session(engine) as session:
            session.add(AudioBook(title="Pointless Meetings", audio=fake_audio_long))
            with pytest.raises(
                DurationValidationError,
                match="Maximum allowed duration is: 2000, but 2099 is given.",
            ):
                session.flush()

    def test_valid_audio(self, fake_valid_audio) -> None:
        with Session(engine) as session:
            session.add(AudioBook(title="Pointless Meetings", audio=fake_valid_audio))
            session.flush()
            book = session.execute(
                select(AudioBook).where(AudioBook.title == "Pointless Meetings")
            ).scalar_one()
            assert book.audio.file is not None

    def teardown_method(self, method):
        for obj in StorageManager.get().list_objects():
            obj.delete()
        StorageManager.get().delete()
        Base.metadata.drop_all(engine)
